 class BaseScenarioProvider {    private static final String MOCK_MODULES_PATH = "/mocks/modules/";    protected FreemarkerProcessor processor;    private ExpectationBuilder builder = new ExpectationBuilder();    public ExpectationTO mockSimpleJsonResponse(String urlBeginWith, Object jsonObject) {        builder.when().url().beginsWith(urlBeginWith).matches().                then().respond().withBody(jsonObject)                .asJson()                .contentType("application/json")                .statusCode(200).complete();        return (ExpectationTO) builder.build();    }    public ExpectationTO mockSimplePayloadResponse(String urlBeginWith, String payload) {        builder.when().url().beginsWith(urlBeginWith).matches().                then().respond().withBody(payload)                .contentType("application/json")                .statusCode(200).complete();        return (ExpectationTO) builder.build();    }    public String loadPayload(String template) throws IOException, TemplateException {        return loadPayload(template, null);    }    public String loadPayload(String template, Map<String,Object> values) throws IOException, TemplateException {        return processor.processTemplate(template, MOCK_MODULES_PATH, values);    }    public void setProcessor(FreemarkerProcessor processor) {        this.processor = processor;    }}